Parse.initialize("Ih70t530LwJAnRJungwuPtE2nE3eakzmwVuZHb6O","sE6Y6iMcoTTa9Z3pBCaAtwnD9F1L9SlWHBKlDQ7h");function onButtonClick(){document.getElementById("yesEmail").style.display="none";document.getElementById("invalidEmail").style.display="none";if(validateEmail()){resetPass()}}function resetPass(){Parse.User.requestPasswordReset(document.getElementById("validEmail").value,{success:function(){document.getElementById("yesEmail").style.display="block";document.getElementById("validEmail").style.border="2px inset"},error:function(a){console.log("Error: "+a.code+" "+a.message);if(a.code==125){document.getElementById("invalidEmail").style.display="block";document.getElementById("validEmail").style.border="1px solid";document.getElementById("validEmail").style.borderColor="#b94a48"}}})}function validateEmail(){var a=document.getElementById("validEmail");if(a.value==""){a.style.border="1px solid";a.style.borderColor="#b94a48";document.getElementById("noEmail").style.display="block";return false}else{document.getElementById("noEmail").style.display="none";a.style.border="2px inset";return true}};